head.script:
                        // Initialize and add the map
                        function initMap() {
                            // The location of Uluru
                            const uluru = {
                                lat: 5.376066,
                                lng: -3.993204
                            };
                            const agence1 = {
                                lat: 5.3742433146325865,
                                lng: -3.990339127890237
                            };
                            const agence2 = {


                                lat: 5.3767266656381905,
                                lng: -4.0100321916792145
                            };
                            const agence3 = {

                                lat: 5.381316574709637,
                                lng: -4.004757830555943
                            };
                            const agence4 = {
                                lat: 5.359444534812267,
                                lng: -3.978901457538368
                            };
                            // The map, centered at Uluru
                            const map = new google.maps.Map(document.getElementById("map"), {
                                zoom: 10,
                                center: uluru,
                                mapTypeId: "roadmap",

                            });
                            const image = {
                                url: 'CashIn/static/assets/img/localiser.png',
                                scaledSize: new google.maps.Size(70, 70),
                                origin: new google.maps.Point(0.0),
                                anchor: new google.maps.Point(0.0)
                            }
                            const image1 = {
                                    url: 'CashIn/static/assets/img/position.png',
                                    scaledSize: new google.maps.Size(75, 90),
                                    origin: new google.maps.Point(0.0),
                                    anchor: new google.maps.Point(0.0)
                                }
                                // The marker, positioned at Uluru
                            const marker = new google.maps.Marker({
                                position: uluru,
                                map: map,
                                icon: image,
                                label: {
                                    text: "Ma position ",
                                    color: "blue",
                                    fontSize: "20px",
                                    fontWeight: 'bold',
                                },
                            });
                            const marker2 = new google.maps.Marker({
                                position: agence1,
                                map: map,
                                icon: image1,

                            });
                            const marker3 = new google.maps.Marker({
                                position: agence2,
                                map: map,
                                icon: image1,

                            });
                            const marker4 = new google.maps.Marker({
                                position: agence3,
                                map: map,
                                icon: image1,

                            });
                            const marker5 = new google.maps.Marker({
                                position: agence4,
                                map: map,
                                icon: image1,

                            });

                            google.maps.event.addListener(marker2, 'click', popup);
                            google.maps.event.addListener(marker3, 'click', popup);
                            google.maps.event.addListener(marker4, 'click', popup);
                            google.maps.event.addListener(marker5, 'click', popup);


                            function popup() {
                                $('#exampleModal').modal('show')
                            }



                            const inputText = document.createElement("input")
                            inputText.type = "text";
                            inputText.placeholder = "Commune,quartier,type d'agence"

                            map.controls[google.maps.ControlPosition.TOP_CENTER].push(inputText)

                            var btn = document.createElement('div')
                            btn.classList.add('ok');
                            btn.id = 'list'

                            btn.innerHTML = 'Afficher la liste  format_list_bulleted'
                            map.controls[google.maps.ControlPosition.BOTTOM_CENTER].push(btn);

                            btn.onclick = function quoi() {
                                location.href = "listeagence"

                            }


                        }
